mapper.insert:
insert into movie(
movie_no, movie_name, movie_director, movie_release_date,
movie_time, movie_level, movie_nation, movie_content
)
values(
#{movieNo}, #{movieName},#{movieDirector}, #{movieReleaseDate},
#{movieTime}, #{movieLevel}, #{movieNation}, #{movieContent}
)

mapper.update:
update movie set
movie_no = #{dto.movieNo},
movie_name = #{dto.movieName}, movie_release_date = #{dto.movieReleaseDate}, movie_director = #{dto.movieDirector}, movie_time = #{dto.movieTime}, movie_level = #{dto.movieLevel}, movie_nation = #{dto.movieNation}, movie_content = #{dto.movieContent} where movie_no = #{movieNo}

mapper.select:
select * from (
select rownum rn, TMP.* from(
select * from movie_list where instr(lower(movie_name), lower(#{movieName}))
> 0
)TMP
) where rn between ${begin} and ${end}
