mapper.insert:
insert into review_like (
	review_no, member_nickname
) values (
	#{reviewNo}, #{memberNickname}
)

mapper.delete:
delete from review_like
	where review_no = #{reviewNo}
		and member_nickname = #{memberNickname}

mapper.select:
select count(*) > 0
from review_like
where review_no = #{reviewNo}
and member_nickname = #{memberNickname}
