mapper.insert:
insert into review (
	review_no, member_id, member_nickname, 
	movie_no, review_content
) values (
	review_seq.nextval, #{memberNo}, #{memberNickname}, 
	#{movieNo}, #{reviewContent}
)

mapper.update:
update review 
	set review_content = #{reviewContent}
	where review_no = #{reviewNo}
